import React, { useState, useEffect } from 'react';
import ProductDetail from './ProductDetail'
import ProductContainer from './ProductContainer'
import Cart from './Cart'
const Home=()=>{
    let initTodo;
    var[val,valChange]=useState({x:1});
    if (localStorage.getItem("todos") === null) {
      initTodo = [];
    }
    else {
      initTodo = JSON.parse(localStorage.getItem("todos"));
    }
        var g=[{}];
        const [todos, setTodos] = useState(initTodo);
  
        const productDetail = (name,img,description,price,quantity) => {
        console.log("I am adding this todo", img,name,description,price,quantity)
        let sno;
        if (todos.length === 0) {
          sno = 0;
        }
        else {
          sno = todos[todos.length - 1].sno + 1;
        }
        const myTodo = {
          sno: sno,
          name:name,
          img:img.file,
          description:description,
          price:price,
          quantity:quantity,
        }
        setTodos([...todos, myTodo]);
        console.log(myTodo);
      }
      
      const onSubVal = (todo,state) => {
        console.log("I am ondelete of todo", state,todo);
        g=todos;
        var x;
        console.log("g value =",g);
        for(var i=0;i<g.length;i++)
        {
          if(g[i].sno==todo.sno){
            x=i;
            g[i].quantity=g[i].quantity - state;
            console.log("for loop run")
          }
        }
        setTodos(g);
        valChange(g[x]);
    }

     useEffect(() => {
        console.log("effect run");
        localStorage.setItem("todos", JSON.stringify(todos));
      }, [todos,val]);
    
    return(
        <>
           <div className="container-fluid">
                <div className='row'>

                    <div className='col-md-4 '>
                       <div className='row'>
                            <div className='col-12'>
                            <ProductDetail productDetail={productDetail}/>
                            </div>
                        </div>
                        <div className="row">
                            <div className='col-md-12 bg-success'>
                                <Cart/> 
                            </div>
                        </div> 
                    </div>
                    
                    <div className='col-md-8 bg-primrary'>
                        <ProductContainer todos={todos} onSubVal={onSubVal} />
                    </div> 
                </div>
            </div>
        </>
    );
}

export default Home;
