import React, {useState} from 'react'
import "./ProductItem.css"

const ProductItem =({todo,onSubVal})=>{
    const [state, setState] = useState(0);
    var Sub=()=>{
        if(state>0){
            
            setState(state-1)
        }
        else{
            alert("select a value");
        }
    }
    var Add=()=>{
        if(state<todo.quantity){
            setState(state+1)
        }
        else{
            alert("cannot exceed the limit");
        }
    }

    return(
    <>
        <div className="card mb-3 " >
            <div className="row g-0">
                <div className="col-md-4 d-flex  align-items-center img_col">
                    <div className="w-100 mx-1 h-100 d-flex align-items-center justify-content-center"><img src={todo.img} alt="..." style={{ minHeight: 197,borderRadius:10}} className="img-fluid "/></div>
                </div>
                <div className="col-md-8">
                    <div className="card-body">
                        <div className="detail">
                            <div className="d-flex justify-content-between text-color-white align-items-center ">    
                                <div className="linear"><span className="heading">product</span> : <span>{todo.name}</span></div>
                                <div >Desc: {todo.description}</div>
                            </div>
                            <div className="d-flex justify-content-between ">    
                                <div >Price: {todo.price}</div>
                                <div >Quantity: {todo.quantity}</div>
                            </div>
                        </div>
                    </div>
                    <div className="d-flex my-2">
                        <span className="mx-3">select quantity:</span>
                        <span><button type="button" onClick={()=>{Add()}} class="btn btn-primary">+</button></span>
                        <span><button type="button" class="btn ">{state}</button></span>
                        <span><button type="button" onClick={()=>{Sub()}} class="btn btn-primary">−</button></span>
                    </div>
                    <button className="Add_btn float-end mx-3 my-2" onClick={()=>{onSubVal(state,todo);}}>Add to Cart</button>
                </div>
            </div>
        </div>
    </>);
}

export default ProductItem;
