import React, { useState, useEffect } from 'react';
import ProductDetail from './ProductDetail'
import ProductContainer from './ProductContainer'


const Home=()=>{
    let initTodo;
    var[val,valChange]=useState({x:1});
    if (localStorage.getItem("todos") === null) {
      initTodo = [];
    }
    else {
      initTodo = JSON.parse(localStorage.getItem("todos"));
    }
        var g=[{}];
        const [todos, setTodos] = useState(initTodo);
  
        const productDetail = (name,description,price,quantity) => {
        console.log("I am adding this todo", name,description,price,quantity)
        let sno;
        if (todos.length === 0) {
          sno = 0;
        }
        else {
          sno = todos[todos.length - 1].sno + 1;
        }
        const myTodo = {
          sno: sno,
          name:name,
          
          description:description,
          price:price,
          quantity:quantity,
        }
        setTodos([...todos, myTodo]);
        console.log(myTodo);
      }
      
      const onDelete = (todo) => {
        console.log("I am ondelete of todo", todo);
        // Deleting this way in react does not work
        // let index = todos.indexOf(todo);
        // todos.splice(index, 1);
        g=todos;
        var x;
        for(var i=0;i<g.length;i++)
        {
          if(g[i]==todo){
            x=i;
            g[i].title="robin ka japa";
          }
        }
        setTodos(g);
        valChange(g[x
        ]);
    }

     useEffect(() => {
        console.log("effect run");
        localStorage.setItem("todos", JSON.stringify(todos));
      }, [todos,val]);
    
    return(
        <>
            <div className="container-fluid">
                <div className='row'>
                    <div className='col-md-3 '>
                        <ProductDetail productDetail={productDetail}/>
                    </div>
                    <div className='col-md-6 bg-primrary'>
                        <ProductContainer todos={todos} onDelete={onDelete} />
                    </div>
                    <div className='col-md-3 bg-success'>
                        
                    </div>
                </div>
            </div>
        </>
    );
}

export default Home;
