import React, { useState, useEffect } from 'react';
import ProductDetail from './ProductDetail'
import ProductContainer from './ProductContainer'
import Cart from './Cart'
const Home=()=>{
    let initTodo;
    var g=[{}];
    var[val,valChange]=useState(1);
    if (localStorage.getItem("todos") === null) {
      initTodo = [];
    }
    else {
      initTodo = JSON.parse(localStorage.getItem("todos"));
    }
        
        const [todos, setTodos] = useState(initTodo);
        var [cartdata,setCartData] =useState();
  
        const productDetail = (name,img,description,price,quantity) => {
        console.log("I am adding this todo", img,name,description,price,quantity)
        let sno;
        if (todos.length === 0) {
          sno = 0;
        }
        else {
          sno = todos[todos.length - 1].sno + 1;
        }
        const myTodo = {
          sno: sno,
          name:name,
          img:img.file,
          description:description,
          price:price,
          quantity:quantity,
        }
        setTodos([...todos, myTodo]);
        console.log("my todo going to add ",myTodo);
      }
    
      const onSubVal = (x,todo) => {
        console.log("I am ondelete of todo", x,todo);
        g=todos;
        var no;

        console.log("g value =",g);
            for(var i=0;i<todos.length;i++){
                if(todos[i]===todo){
                  console.log("quanttirioroei ==",typeof(todos[i].quantity))
                    no=parseInt(todos[i].quantity)-x;
                      no=no.toString();
                      console.log("no. change ",no);
                      todos[i].quantity=no;
                }
                setCartData(todo);
            }
       
        setTodos(todos);
        
        valChange(val+1);
    }

     useEffect(() => {
        console.log("effect run");
        localStorage.setItem("todos", JSON.stringify(todos));
      }, [todos,val]);
    
    return(
        <>
           <div className="container-fluid">
                <div className='row'>

                    <div className='col-md-4 '>
                       <div className='row'>
                            <div className='col-12'>
                            <ProductDetail productDetail={productDetail}/>
                            </div>
                        </div>
                        <div className="row">
                            <div className='col-md-12'>
                                <Cart CartData={cartdata}/> 
                            </div>
                        </div> 
                    </div>
                    
                    <div className='col-md-8 bg-primrary'>
                        <ProductContainer todos={todos} onSubVal={onSubVal} />
                    </div> 
                </div>
            </div>
        </>
    );
}

export default Home;
